package ru.otus.eba.util;

import ru.otus.eba.dto.openapi.CryptoKeysDto;

/**
 * Класс-утилита для тестовых ключей.
 */
public class TestKeysUtils {
    public static final String TEST_PUBLIC_KEY = "";
    public static final String TEST_PRIVATE_KEY = "";
    public static final String TEST_ENCRYPTED_BY_PRIVATE_KEY = "";
    public static final String TEST_ENCRYPTED_BY_PUBLIC_KEY = "";



    public static CryptoKeysDto createCryptoKeysDto(){
        return new CryptoKeysDto()
                .publicKey(TEST_PUBLIC_KEY)
                .privateKey(TEST_PRIVATE_KEY);
    }
}
